&lt;td style=&#34;width:40px; text-align: right; border: 1px solid gray; color: #ffffff; background: #cc0000;&#34;&gt;&amp;nbsp;&lt;/td&gt;
&lt;td style=&#34;width:40px; text-align: right; border: 1px solid gray; background: #ff0000;&#34;&gt;&amp;nbsp;&lt;/td&gt;
&lt;td style=&#34;width:40px; text-align: right; border: 1px solid gray; background: #ff3232;&#34;&gt;&amp;nbsp;&lt;/td&gt;
&lt;td style=&#34;width:40px; text-align: right; border: 1px solid gray; background: #fe6666;&#34;&gt;&amp;nbsp;&lt;/td&gt;
&lt;td style=&#34;width:40px; text-align: right; border: 1px solid gray; background: #ff9898;&#34;&gt;&amp;nbsp;&lt;/td&gt;
